import React, { useState } from "react";
import axios from "axios";
import {useNavigate} from 'react-router-dom'


function Login({ setToken, setUserDetails }) {

    const[username, setUsername] = useState('');
    const[password, setPassword] = useState('');
    const[message, setMessage] = useState('');
    const navigate = useNavigate()

    const handlelogin = async(e) => {
        e.preventDefault();
        try{
        const response = await axios.post('http://127.0.0.1:8000/api/login/', {username, password}, {
            headers : {'Content-Type' : 'application/x-www-form-urlencoded; charset=UTF-8'}
        });
        const {token, user_id, email, is_admin} = response.data;
        setToken(token);
        console.log(token)
        localStorage.setItem('token', token);
        localStorage.setItem('loginSuccess', 'true')
        setUserDetails({user_id, email, is_admin})
        navigate('/')
    }
    catch(error){
        console.log(error)
        if (error.response && error.response.data) {
            setMessage(`Login failed: ${error.response.data.detail || 'Incorrect username or password'}`);
        }
        else{
        setMessage('login failed');
        }
    }
    };


    return(
        <div>
            <h2>Login Page</h2>
            <form onSubmit={handlelogin}>
                <input type='text' placeholder= 'Enter Username' value = {username} onChange={(e) => setUsername(e.target.value)} />
                <input type='password' placeholder='Enter Password' value ={password} onChange={(e) => setPassword(e.target.value)} />
                <button type='submit'>Login</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    )


}

export default Login;
