import axios from "axios";
import React, { useEffect, useState } from "react";

function MyBooking() {
    const [bookings, setBookings] = useState([]);

    useEffect(() => {
        const token = localStorage.getItem('token');
        axios.get('http://127.0.0.1:8000/api/my_bookings/', {
            headers: {
                'Authorization': `Bearer ${token}`,
            },
        })
        .then(response => {
            setBookings(response.data);
        })
        .catch(error => {
            console.error('Error fetching bookings', error);
        });
    }, []);

    async function handleCancelTicket(bookId) {
        try {
            const token = localStorage.getItem('token');
            await axios.delete('http://127.0.0.1:8000/api/my_bookings/', {
                data: { booking_id: bookId },
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });
            alert('Booking Cancelled');
            setBookings(bookings.filter(booking => booking.id !== bookId));
        } catch (error) {
            console.log('Error cancelling booking', error);
            alert('Error cancelling booking');
        }
    }

    return (
        <div>
            <h2>Bookings</h2>
            {bookings.filter(booking => booking.cancelled !== true).map((list) => (
                <div key={list.id}>
                    <p>{list.movie.title}</p>
                    <p>{list.booked_at}</p>
                    <p>{list.booked_slot}</p>
                    <button onClick={() => handleCancelTicket(list.id)}>Cancel Ticket</button>
                </div>
            ))}
        </div>
    );
}

export default MyBooking;
