// import logo from './logo.svg';
import './App.css';
import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate} from 'react-router-dom';
import MovieList from './components/movielist';
import Register from './components/register';
import Login from './components/login';
import { useState } from 'react';
import Home from './components/home';
import Nav from './components/nav';
import Movie from './components/moive';
import Booking from './components/booking';
import UploadMovie from './components/uploadMovie';

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

function AppContent() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [role, setRole] = useState(localStorage.getItem('role'));
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/');
};

  return (
    <div className="App">
      <Nav token={token} handleLogout={handleLogout} role={role} />
      <Routes>
        <Route path='/' element={<Home token={token} handleLogout={handleLogout} />} />
        <Route path='/register' element={<Register />} />
        <Route path='/login' element={<Login setToken={setToken} setRole={setRole} />} />
        <Route path='/movieslist' element={<MovieList />} />
        <Route path='/movie/:id' element={<Movie />} />
        <Route path='/booking' element={<Booking />} />
        {role === 'admin' && <Route path='/upload' element={<UploadMovie />} />}
      </Routes>
    </div>
  );
}

export default App;
