import React from "react";
import { Link } from "react-router-dom";

function Nav({token, handleLogout, role}){
    return(
        <div>
                  <nav className="navbar navbar-expand-lg" style={{ backgroundColor: "#3a71d8" }}>
        <div className="container-fluid">
          <Link className="navbar-brand text-white" to={"/"}>Movie Booking</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-end" id="navbarNavAltMarkup">
            <div className="navbar-nav">
              <a className="nav-link active text-white" aria-current="page" href="/">Home</a>
              {!token && (
                <>
                  <Link className="nav-link active text-white" aria-current="page" to={"/register"}>Register</Link>
                  <Link className="nav-link active text-white" aria-current="page" to={"/login"}>Login</Link>
                </>
              )}
              {token && (
                <>
                 {role === 'admin' && <li><Link to="/upload">Upload Movie</Link></li>}
                <Link className="nav-link active text-white" aria-current="page" to={"/mybookings"}>My Booking</Link>
                <button className="nav-link active text-white" aria-current="page" onClick={handleLogout}>Log Out</button>
                </>
              )}
            </div>
          </div>
        </div>
      </nav>
        </div>
    )
}

export default Nav;
