// import logo from './logo.svg';
import './App.css';
import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate} from 'react-router-dom';
import MovieList from './components/movielist';
import Register from './components/register';
import Login from './components/login';
import { useState } from 'react';
import Home from './components/home';
import Nav from './components/nav';
import Movie from './components/moive';
import Booking from './components/booking';
import My_booking from './components/mybooking';

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

function AppContent() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken(null);
    navigate('/');
  };

  return (
    <div className="App">
      <Nav token={token} handleLogout={handleLogout} />
      <Routes>
        <Route path='/' element={<Home token={token} handleLogout={handleLogout} />} />
        <Route path='/register' element={<Register />} />
        <Route path='/login' element={<Login setToken={setToken} />} />
        <Route path='/movieslist' element={<MovieList setToken={setToken} />} />
        <Route path='/movie/:id' element={<Movie />} />
        <Route path='/booking' element={<Booking />} />
        <Route path='/mybookings' element={<My_booking setToken={setToken}/>} />
      </Routes>
    </div>
  );
}

export default App;
