// import logo from './logo.svg';
import './App.css';
import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate} from 'react-router-dom';
import MovieList from './components/movielist';
import Register from './components/register';
import Login from './components/login';
import { useState } from 'react';
import Home from './components/home';
import Nav from './components/nav';
import Movie from './components/moive';
import Booking from './components/booking';
import My_booking from './components/mybooking';

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

function AppContent() {
  const [token, setToken] = useState(localStorage.getItem('token'));
    const [userDetails, setUserDetails] = useState(null);
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('token');
        setToken(null);
        setUserDetails(null);
        navigate('/');
    };

    return (
        <Router>
            <div className="App">
                <Nav token={token} handleLogout={handleLogout} isAdmin={userDetails?.is_admin} />
                <Routes>
                    <Route path='/' element={<Home token={token} handleLogout={handleLogout} />} />
                    <Route path='/register' element={<Register />} />
                    <Route path='/login' element={<Login setToken={setToken} setUserDetails={setUserDetails} />} />
                    <Route path='/movieslist' element={<MovieList />} />
                    <Route path='/movie/:id' element={<Movie />} />
                    <Route path='/booking' element={<Booking />} />
                    {userDetails?.is_admin && <Route path='/upload' element={<UploadMovie />} />}
                </Routes>
            </div>
        </Router>
    );
}

export default App;
