import React from "react";
import { useNavigate } from "react-router-dom";

function Nav({token}){

  const navi = useNavigate();

  const handleLogout = () =>{
    localStorage.removeItem('token');
    setToken(null)
    navi('/')
  }
    return(
        <div>
                  <nav className="navbar navbar-expand-lg" style={{ backgroundColor: "#3a71d8" }}>
        <div className="container-fluid">
          <a className="navbar-brand text-white" href="/">Movie Booking</a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-end" id="navbarNavAltMarkup">
            <div className="navbar-nav">
              <a className="nav-link active text-white" aria-current="page" href="/">Home</a>
              {!token && (
                <>
                  <a className="nav-link active text-white" aria-current="page" href="/register">Register</a>
                  <a className="nav-link active text-white" aria-current="page" href="/login">Login</a>
                </>
              )}
              {token && (
                <button className="nav-link active text-white" aria-current="page" onClick={handleLogout}>Log Out</button>
              )}
            </div>
          </div>
        </div>
      </nav>
        </div>
    )
}

export default Nav;
