import axios from "axios";
import React, { useState } from "react";

function UploadMovie() {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [duration, setDuration] = useState('');
    const [slot, setSlot] = useState('');
    const [start, setStart] = useState('');
    const [end, setEnd] = useState('');
    const [images, setImage] = useState([]);
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        const role = localStorage.getItem('role');
        if (role !== 'admin') {
            setMessage('You are not authorized to upload movies.');
            return;
        }

        const formData = new FormData();
        formData.append('title', title);
        formData.append('description', description);
        formData.append('duration', duration);
        formData.append('slot', slot);
        formData.append('start', start);
        formData.append('end', end);
        images.forEach(image => {
            formData.append('images', image);
        });

        const token = localStorage.getItem('token');
        console.log('Token:', token); // Debug: Check if token is retrieved correctly

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/upload/', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'Authorization': `Token ${token}`,
                }
            });
            console.log(response.data);
            setMessage('Movie uploaded successfully');
        } catch (error) {
            console.error('Error uploading movie:', error);
            console.log('Response:', error.response); // Debug: Log response for more details
            setMessage('Error uploading movie');
        }
    };

    const handleImages = (e) => {
        const files = Array.from(e.target.files);
        setImage(files);
    }

    return (
        <div>
            <h2>Upload Movie Details</h2>
            <form onSubmit={handleSubmit}>
                <input type='text' placeholder='Movie Title' value={title} onChange={(e) => setTitle(e.target.value)} />
                <textarea placeholder='Description' value={description} onChange={(e) => setDescription(e.target.value)} />
                <input placeholder="Movie Duration" type='number' value={duration} onChange={(e) => setDuration(e.target.value)} />
                <input placeholder="Number Of Slot" type='number' value={slot} onChange={(e) => setSlot(e.target.value)} />
                <input type='time' placeholder='Start Time' value={start} onChange={(e) => setStart(e.target.value)} />
                <input type='time' placeholder='End Time' value={end} onChange={(e) => setEnd(e.target.value)} />
                <input type='file' multiple onChange={handleImages} />
                <button type='submit'>Upload</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}

export default UploadMovie;
