import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

function Movie() {
    const { id } = useParams();
    const [movie, setMovie] = useState(null);
    const [slot, setSlot] = useState('');
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        axios.get(`http://127.0.0.1:8000/api/movie/${id}/`, {
            headers: {
                'Authorization': `Bearer ${token}`,
            },
        })
            .then(response => {
                setMovie(response.data);
            })
            .catch(error => {
                setError('Error fetching movie');
                console.log('Error fetching', error);
            });
    }, [id]);

    if (error) {
        return <p>{error}</p>;
    }

    if (!movie) {
        return <p>Loading.....</p>;
    }

    function change() {
        var book = document.getElementById('bok');
        var movie = document.getElementById('mov');
        if (movie.style.display !== 'none') {
            book.style.display = 'block';
            movie.style.display = 'none';
        } else {
            book.style.display = 'none';
            movie.style.display = 'block';
        }
    }

    function handleBooking(e) {
        e.preventDefault();
        const movieID = id;
        const token = localStorage.getItem('token');
        if (!token) {
            alert('Please log in to book a slot');
            navigate('/login'); // Redirect to login page if not authenticated
            return;
        }
        axios.post('http://127.0.0.1:8000/api/book_slot/', {
            movie_id: movieID,
            slot: slot
        }, {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        })
            .then(response => {
                alert('Ticket Booked');
                navigate('/booking');
            })
            .catch(error => {
                console.log('Booking error:', error);
                alert('Error booking');
            });
    }

    return (
        <div>
            <div id='mov'>
                <h2>{movie.title} Movie</h2>
                <p>{movie.description}</p>
                <p>{movie.available_slots}</p>
                <button onClick={change}>Book Slot</button>
            </div>
            <div style={{ display: 'none' }} id='bok'>
                <p>Total Slot: {movie.available_slots}</p>
                <form onSubmit={handleBooking}>
                    <input name="slot" type='number' value={slot} onChange={(e) => setSlot(e.target.value)}></input>
                    <button type='submit'>Book Now!!</button>
                    <button type='button' onClick={change}>Back</button>
                </form>
            </div>
        </div>
    );
}

export default Movie;
