import React, { useEffect, useState } from "react";

function Home({ token, handleLogout }) {
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    if (localStorage.getItem('loginSuccess') === 'true') {
      setShowModal(true);
      localStorage.removeItem('loginSuccess');
    }
  }, []);

  const handleClose = () => {
    setShowModal(false);
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg" style={{ backgroundColor: "#3a71d8" }}>
        <div className="container-fluid">
          <a className="navbar-brand text-white" href="/">Movie Booking</a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-end" id="navbarNavAltMarkup">
            <div className="navbar-nav">
              <a className="nav-link active text-white" aria-current="page" href="/">Home</a>
              {!token && (
                <>
                  <a className="nav-link active text-white" aria-current="page" href="/register">Register</a>
                  <a className="nav-link active text-white" aria-current="page" href="/login">Login</a>
                </>
              )}
              {token && (
                <button className="nav-link active text-white" aria-current="page" onClick={handleLogout}>Log Out</button>
              )}
            </div>
          </div>
        </div>
      </nav>

      <a className="btn btn-primary" href="movieslist">View All Movies </a>

      {showModal && (
        <div className="modal show" style={{ display: 'block', background: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Notification</h5>
                <button type="button" className="btn-close" onClick={handleClose}></button>
              </div>
              <div className="modal-body">
                <p>Login Successful</p>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-primary" onClick={handleClose}>OK</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Home;
