import axios from 'axios';
import React, { useEffect, useState } from 'react';

function MovieList() {
    const [movies, setMovies] = useState([]);
    const [error, setError] = useState(null);

    const token = localStorage.getItem('token');

    useEffect(() => {
        // Define the Axios client
        const apiClient = axios.create({
            baseURL: 'http://127.0.0.1:8000/api/',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`,
            },
        });

        // Example POST request (if you actually need to use POST method)
        apiClient.post('/movies/', {
            // data: { your_payload_key: 'your_payload_value' } // Add data payload if needed
        })
        .then(response => {
            setMovies(response.data);
        })
        .catch(error => {
            console.error('There was an error fetching the movies!', error);
            setError('There was an error fetching the movies!');
        });

    }, [token]);

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div>
            {movies.map(movie => (
                <div key={movie.id}>
                    <h2>{movie.title}</h2>
                    <p>{movie.description}</p>
                </div>
            ))}
        </div>
    );
}

export default MovieList;
